import { useFormik } from "formik";
import { signupschema } from "../schemas"; 

const onSubmit=()=>{
  console.log("submitted");
};
const BasicForm = () => {
  const {values,errors,handleBlur,handleChange,handleSubmit}=useFormik({
    initialValues:{
      email:"",
      username:"",
      password:"",
      confirmpassword:"",
      age:"",
    },
    validationSchema: signupschema,
    onSubmit,
  });
//to validate we should use a schema that will tell us the validation of every input field
 console.log(errors);
return (
    <form onSubmit={signupschema} autoComplete="off">
      <label htmlFor="email">Email</label>
      <input 
      value={values.email}
      onChange={handleChange} //set the email value with the inserted value whenever it changes
      id="email" 
      type="email"
      placeholder="Enter your email" 
      onBlur={handleBlur}
      />

      <label htmlFor="username">Username</label>
      <input 
      value={values.username}
      onChange={handleChange} 
      id="username" 
      type="username"
      placeholder="Enter your Username" 
      onBlur={handleBlur}
      />

      <label htmlFor="password">Password</label>
      <input 
      value={values.password}
      onChange={handleChange} 
      id="password" 
      type="password"
      placeholder="Enter your password" 
      onBlur={handleBlur}
      />

<label htmlFor="confirmpassword">Confirm Password</label>
      <input 
      value={values.confirmpassword}
      onChange={handleChange} 
      id="confirmpassword" 
      type="confirmpassword"
      placeholder="confirm your password" 
      onBlur={handleBlur}
      />

      <label htmlFor="age">Age</label>
      <input 
      value={values.age}
      onChange={handleChange} 
      id="age" 
      type="age"
      placeholder="Enter your age" 
      onBlur={handleBlur}
      />
      <button type="submit">SignUp</button>
    </form>
  );
};
export default BasicForm;
