import React from 'react';
import { Routes, Route } from 'react-router-dom';
import regimg from './assets/3456426.png';
import { Signup } from './components/Signup';
import { Login } from './components/login';
import Home from './components/Home';
import {Dashboard} from './components/Dashboard'; // Create this new component
import './App.css';

function App() {
  const location = useLocation();
  const showImage = location.pathname === '/signup';
  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-md-6 my-auto">
          <img className="img-fluid w-100" src={regimg} alt="Authentication"/>
        </div>
        <div className="col-md-6">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route path="/dashboard" element={<Dashboard />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
