import * as yup from "yup";
const passwordRules = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{5,}$/; // min 5 characters, 1 upper case letter, 1 lower case letter, 1 numeric digit.

export const signupschema = yup.object().shape({
  email: yup.string().email("Please Enter a Valid Email.").required("Required"),
  age: yup.number().positive().integer().required(),
  password: yup.string().min(5).matches(passwordRules, {
    message: "Please Enter a Stronger Password!"
  }).required("Required"),
  confirmpassword: yup.string().oneOf([yup.ref('password'), null], "Password Must Match The Previous One!").required("Required")
});
