import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

export const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [showProfile, setShowProfile] = useState(false);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user')) || {};
    setUser(storedUser);
    
    // Redirect to login if no user data
    if (!storedUser.email) {
      navigate('/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const toggleProfile = () => {
    setShowProfile(!showProfile);
  };

  const handleProfileUpdate = (updatedUser) => {
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));
    setShowProfile(false);
  };

  return (
    <div className="dashboard">
      <nav className="dashboard-nav">
        <div className="nav-brand">
          Welcome{user.firstName ? `, ${user.firstName}` : ''}!
        </div>
        <div className="nav-buttons">
          <button onClick={toggleProfile} className="nav-btn profile-btn">
            Profile
          </button>
          <button onClick={handleLogout} className="nav-btn logout-btn">
            Logout
          </button>
        </div>
      </nav>

      <div className="dashboard-content">
        {showProfile ? (
          <ProfileSection 
            user={user} 
            onUpdate={handleProfileUpdate}
          />
        ) : (
          <div className="main-content">
            <h2>Dashboard Content</h2>
            <p>This is your main dashboard area.</p>
          </div>
        )}
      </div>
    </div>
  );
};

const ProfileSection = ({ user, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: user.firstName || '',
    lastName: user.lastName || '',
    email: user.email || '',
    profilePicture: user.profilePicture || ''
  });

  const getInitials = () => {
    const firstInitial = formData.firstName ? formData.firstName.charAt(0) : '';
    const lastInitial = formData.lastName ? formData.lastName.charAt(0) : '';
    return `${firstInitial}${lastInitial}`;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8081/profile/update', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          email: user.email // Keep original email as identifier
        }),
      });
      
      const data = await response.json();
      if (data.success) {
        const updatedUser = { ...user, ...formData };
        onUpdate(updatedUser);
        setIsEditing(false);
      } else {
        alert('Update failed: ' + (data.message || 'Unknown error'));
      }
    } catch (error) {
      console.error('Update error:', error);
      alert('Update failed. Please try again.');
    }
  };

  return (
    <div className="profile-section">
      <h2>Profile Information</h2>
      
      {isEditing ? (
        <form onSubmit={handleSubmit} className="profile-form">
          <div className="profile-picture-container">
            {formData.profilePicture ? (
              <img 
                src={formData.profilePicture} 
                alt="Profile" 
                className="profile-picture"
              />
            ) : (
              <div className="profile-picture-placeholder">
                {getInitials()}
              </div>
            )}
            <input
              type="text"
              name="profilePicture"
              value={formData.profilePicture}
              onChange={handleInputChange}
              placeholder="Paste image URL here"
              className="picture-url-input"
            />
          </div>
          
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
              disabled
            />
          </div>
          
          <div className="form-actions">
            <button type="submit" className="save-btn">Save Changes</button>
            <button 
              type="button" 
              onClick={() => setIsEditing(false)}
              className="cancel-btn"
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <div className="profile-view">
          <div className="profile-picture-container">
            {user.profilePicture ? (
              <img 
                src={user.profilePicture} 
                alt="Profile" 
                className="profile-picture"
              />
            ) : (
              <div className="profile-picture-placeholder">
                {getInitials()}
              </div>
            )}
          </div>
          
          <div className="profile-info">
            <p><strong>First Name:</strong> {user.firstName || 'Not provided'}</p>
            <p><strong>Last Name:</strong> {user.lastName || 'Not provided'}</p>
            <p><strong>Email:</strong> {user.email || 'Not provided'}</p>
          </div>
          
          <button 
            onClick={() => setIsEditing(true)}
            className="edit-btn"
          >
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );
};
